mapper.select:
SELECT a.*, u.u_name
FROM article a LEFT JOIN user u ON a.a_writer = u.u_id
WHERE a_id = #{a_id}

mapper.update:
UPDATE article
SET a_click = a_click + 1
WHERE a_id = #{a_id}

UPDATE article
SET a_writer = #{a_writer},
    a_title = #{a_title},
    a_content = #{a_content},
    b_id = #{b_id}
WHERE a_id = #{a_id}
